import React, { useState, useEffect, useCallback } from "react";
const axios = require("axios");

function Post(props) {
  const [post, setPost] = useState({});

  const data = useCallback(() => fetchPostData(), [fetchPostData]);
  useEffect(() => {
    let isLoaded = false;

    data();

    return () => {
      isLoaded = true;
    };
  }, [data]);

  async function fetchPostData() {
    await axios
      .get(`http://localhost:5000/posts/${props.match.params.id}`)
      .then((res) => setPost(res.data))
      .catch((err) => console.log("Error: ", err.message));
  }
  // console.log(props);

  return (
    <div>
      {post.length > 0
        ? post.map((thePost) => {
            return (
              <div className="card my-3" key={thePost._id}>
                <div className="card-body">
                  <h3 className="card-title">{thePost.title}</h3>
                  <h6 className="card-subtitle text-muted">{post.snippet}</h6>
                  <h4 className="card-text ">{thePost.body}</h4>
                </div>
              </div>
            );
          })
        : "No post"}
    </div>
  );
}

export default Post;
