import React, { useState } from "react";
const axios = require("axios");
const { createUser } = require("../api/index");

function UserForm() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });

  const [signUp, setSignUp] = useState(true);

  const handleSubmit = (e) => {
    e.preventDefault();

    let newUser;

    if (signUp.valueOf === true) {
      newUser = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        password: formData.password,
      };
    }

    newUser = {
      email: formData.email,
      password: formData.password,
    };

    console.log(signUp);

    {
      signUp
        ? axios
            .post("http://localhost:5000/user/signUp", newUser)
            .then(() => console.log("Success"))
            .catch((err) => console.log("Error: ", err.message))
        : axios
            .post("http://localhost:5000/user/signIn", newUser)
            .then(() => console.log("Success"))
            .catch((err) => console.log("Error: ", err.message));
    }

    console.log(formData);

    // setFormData({
    //   firstName: "",
    //   lastName: "",
    //   email: "",
    //   password: "",
    // });
  };
  const handleClick = () => {
    const formValues = document.querySelectorAll("input");
    const subBtn = document.querySelector("#subBtn");
    formValues.values = " ";
  };

  return (
    <div className="container my-5 p-5">
      <div className="p-2">
        <button
          className="btn btn-outline-secondary btn-lg btn-block active mx-2"
          id="formBtn"
          onClick={() => setSignUp((prevState) => !prevState)}
        >
          {signUp ? "Sign In" : "Sign Up"}
        </button>
      </div>
      <hr />
      <form onSubmit={handleSubmit}>
        {signUp ? (
          <>
            <div className="form-group">
              <label for="firstNameInput">First Name:</label>
              <input
                type="text"
                class="form-control text-center"
                id="firstNameInput"
                name="firstName"
                placeholder="Please enter your first name"
                onChange={(e) =>
                  setFormData({ ...formData, firstName: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label for="lastNameInput">Last Name:</label>
              <input
                type="text"
                class="form-control text-center"
                id="lastNameInput"
                name="lastName"
                placeholder="Please enter your last name"
                onChange={(e) =>
                  setFormData({ ...formData, lastName: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label for="emailInput">Email:</label>
              <input
                type="email"
                class="form-control text-center"
                id="emailInput"
                name="email"
                placeholder="Please enter your email"
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label for="passwordInput">Password:</label>
              <input
                type="passwprd"
                class="form-control text-center"
                id="passwordInput"
                name="password"
                placeholder="Please enter your password"
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
            <button
              type="submit"
              id="subBtn"
              className="btn btn-primary"
              onClick={handleClick}
            >
              Submit
            </button>
          </>
        ) : (
          <>
            <div className="form-group">
              <label for="emailInput">Email:</label>
              <input
                type="email"
                class="form-control text-center"
                id="emailInput"
                name="email"
                placeholder="Please enter your email"
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label for="passwordInput">Password:</label>
              <input
                type="passwprd"
                class="form-control text-center"
                id="passwordInput"
                name="password"
                placeholder="Please enter your password"
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
            <button
              type="submit"
              id="subBtn"
              className="btn btn-primary"
              onClick={handleClick}
            >
              Submit
            </button>
          </>
        )}
      </form>
    </div>
  );
}

export default UserForm;
